package ArrayMutation;

import java.util.Arrays;

public class App {


    public static void main(String[] args) {
        int[] test={4, 0, 1, -2, 3};
        System.out.println(Arrays.toString(solution(5, test)));
    }

    public static int[] solution(int n, int[] a) {

        int[] b = new int[a.length];
        int firstIndex;
        int finalIndex;
        for(int i=0;i<a.length;i++){

            firstIndex=(i==0)?0:a[i - 1]; // to test first index
            finalIndex=(i+1<a.length)?a[i + 1]:0; // to test final index
            b[i]= firstIndex + a[i] + finalIndex;
        }
        return b;
    }
}

